    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        
        DefaultTableModel table = (DefaultTableModel) tableuser.getModel();
        int selectedRow = -1;
        String uname = "";
        try {
        selectedRow = tableuser.getSelectedRow();
        uname = table.getValueAt(selectedRow, 0).toString();
        } catch (Exception e) {
        }
        if (selectedRow <0 ) {
            
            JOptionPane.showMessageDialog(null, "Select User");
            
        } else {
            int dialogResult = JOptionPane.showConfirmDialog(null, "Do you want to delete User?\n" + uname, "Warning", JOptionPane.YES_NO_OPTION);
        
            if(dialogResult == JOptionPane.YES_OPTION) {
         
            UserFunctions u = new UserFunctions();
            u.deleteUser(uname);
            JOptionPane.showMessageDialog(null, uname + "\nUser Deleted");
            txtuname.setText("");
            txtpass.setText("");
            u.table_update(tableuser);
            }
        }
        
        
    }//GEN-LAST:event_jButton9ActionPerformed

