    private void jButton10ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton10ActionPerformed
        if (comboCategory.getSelectedItem() == null || comboBrand.getSelectedItem() == null || comboSize.getSelectedItem() == null || comboStatus.getSelectedItem() == null ) {
            if(comboCategory.getSelectedItem() == null ){
                JOptionPane.showMessageDialog(null, "Select Catagory");
            }
            else if(comboBrand.getSelectedItem() == null){
                JOptionPane.showMessageDialog(null, "Select Brand");
            }
            else if(comboSize.getSelectedItem() == null ){
                JOptionPane.showMessageDialog(null, "Select Size");
            }
            else if(comboStatus.getSelectedItem() == null){
                JOptionPane.showMessageDialog(null, "Select Status");
            }
        } else {
        String barcode = txtbarcode.getText();
        CategoryFunctions citem = (CategoryFunctions) comboCategory.getSelectedItem();
        BrandFunctions bitem = (BrandFunctions) comboBrand.getSelectedItem();
        Double costPrice = 0.0;
        Double sellPrice = 0.0;
        try {
            costPrice = Double.parseDouble(txtcostprice.getText());
            sellPrice = Double.parseDouble(txtsellprice.getText());
        } catch (Exception e) {
        }
        StatusFunctions sitem = (StatusFunctions) comboStatus.getSelectedItem();
        SizeFunctions sizeitme = (SizeFunctions) comboSize.getSelectedItem();
        if (barcode.equals("")) {
            JOptionPane.showMessageDialog(null, "Empty Barcode");
            txtbarcode.requestFocus();
        } else if (costPrice == 0.0 || sellPrice == 0.0) {
            if (costPrice == 0.0) {
                JOptionPane.showMessageDialog(null, "Enter Cost price");
                txtcostprice.requestFocus();
            } else {
                JOptionPane.showMessageDialog(null, "Enter Sell price");
                txtsellprice.requestFocus();
            }
        } else {
            AddProductFunctions a = new AddProductFunctions(barcode, citem.getCid(), bitem.getId(), costPrice, sellPrice, sitem.getSid(), sizeitme.getSid());
            int i = 2; // valu not dependent now
            String productDetails = "Barcode:     " + barcode + "\n Category:     " + citem.getCname() + "\n Brand:     " + bitem.getName() + "\n CostPrice:     " + costPrice + "\n SellPrice:     " + sellPrice + "\n Size:     " + sizeitme.getSname() + "\n Status:     " + sitem.getSname();
            int dialogResult = JOptionPane.showConfirmDialog(null, "Do you want to Add Product ?\n" + productDetails, "Warning", JOptionPane.YES_NO_OPTION);
            if (dialogResult == JOptionPane.YES_OPTION) {
                i = a.addProduct();
            }else {
                txtbarcode.setText("");
                txtcostprice.setText("");
                txtsellprice.setText("");
                comboBrand.setSelectedIndex(0);
                comboCategory.setSelectedIndex(0);
                comboSize.setSelectedIndex(0);
                comboStatus.setSelectedIndex(0);
                txtbarcode.requestFocus();
            }
            if (i == 1) {
                JOptionPane.showMessageDialog(null, "Product Added");
                a.table_update(tableAddAllProduct);
                txtbarcode.setText("");
                txtcostprice.setText("");
                txtsellprice.setText("");
                comboBrand.setSelectedIndex(0);
                comboCategory.setSelectedIndex(0);
                comboSize.setSelectedIndex(0);
                comboStatus.setSelectedIndex(0);
                txtbrand.requestFocus();
            } else if (i == 0) {
                JOptionPane.showMessageDialog(null, "Barcode Already Exists");
                txtbarcode.setText("");
                txtcostprice.setText("");
                txtsellprice.setText("");
                comboBrand.setSelectedIndex(0);
                comboCategory.setSelectedIndex(0);
                comboSize.setSelectedIndex(0);
                comboStatus.setSelectedIndex(0);
                txtbarcode.requestFocus();
            }
        }
        }

    }//GEN-LAST:event_jButton10ActionPerformed

