    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        DefaultTableModel table = (DefaultTableModel) tableBrand.getModel();
        int selectedRow = -1;
        String bname = "";
        try {
        selectedRow = tableBrand.getSelectedRow();
        bname = table.getValueAt(selectedRow, 0).toString();
        } catch (Exception e) {
        }
        String updateBrand = "";
        updateBrand = txtbrand.getText();
        if (selectedRow <0 || updateBrand.equals("") ) {
            if (selectedRow<0) {
            JOptionPane.showMessageDialog(null, "Select Brand");
            }else{
                JOptionPane.showMessageDialog(null, "Enter New Brand Name");
                txtbrand.requestFocus();
            }
        } else {
            if (bname.toLowerCase().equals(updateBrand.toLowerCase())) {
                JOptionPane.showMessageDialog(null, "Brand Already exists");
                txtbrand.requestFocus();
            } else {
            
            BrandFunctions b = new BrandFunctions();
            b.editBrand(bname, updateBrand);
            JOptionPane.showMessageDialog(null, "Brand Updated");
            txtbrand.setText("");
            b.table_update(tableBrand);
            }
        }
    }//GEN-LAST:event_jButton4ActionPerformed

